mapper.select:
select p.`id`, p.`name`, p.`type`, p.`provincial`, p.`city`, p.`country`, p.`address`, p.`longitude`, p.`latitude`, p.`covered_area` as coveredArea, p.`worker_num` as workerNum, p.`company_id` as companyId, c.`name` as companyName, p.`contacts`, p.`contact_tel` as contactTel, p.`create_time` as createTime, p.`update_time` as updateTime from sp_project p left join sp_company c on p.company_id=c.id
and p.`type` = #{type}
and (p.`name` like concat('%', #{name}, '%') or p.pinyin like concat('%', #{name}, '%'))
and (p.`contacts` like concat('%', #{contacts}, '%') or p.contact_tel like concat('%', #{contacts}, '%'))
and p.`create_time` >= #{ctimeStart}
and p.`create_time` <= #{ctimeEnd}
order by p.`create_time` desc
